mapper.update:
UPDATE GrowUp
SET grow_title =
#{grow_title},
grow_content = #{grow_content}
WHERE grow_num = #{grow_num}

mapper.select:
		SELECT g.*, i.image1, i.image2, i.image3
        FROM growup g
        LEFT JOIN image i ON g.image_folder_num = i.image_folder_num
        WHERE farm_num = #{farm_num}
		ORDER BY bno DESC
		LIMIT #{start}, #{amount}
