mapper.update:
UPDATE myfarm
SET farm_name =
#{farm_name},
farm_intro = #{farm_intro}
WHERE farm_num = #{farm_num}

UPDATE myfarm
SET myfarm_view = myfarm_view+1
WHERE farm_num = #{farm_num}

mapper.select:
		SELECT f.*, i.image1
        FROM myfarm f
        LEFT JOIN image i ON f.image_folder_num = i.image_folder_num WHERE f.farm_num = #{farm_num}

mapper.update:
UPDATE user
SET user_isFarm = 1
WHERE user_num = #{user_num}

UPDATE user
SET user_isFarm = 0
WHERE user_num = #{user_num}
